const TWO = 2

export const BASE_WIDTH = 128
export const LINE_WIDTH = 24
export const OUTER_RADIUS = BASE_WIDTH / TWO
export const INNER_RADIUS = OUTER_RADIUS - (LINE_WIDTH / TWO)
export const CIRCUMFERENCE = TWO * INNER_RADIUS * Math.PI
export const LINE_WIDTH_HALF = LINE_WIDTH / TWO
export const BOTTOM = BASE_WIDTH - LINE_WIDTH_HALF
export const RADIUS_DIFF = OUTER_RADIUS - INNER_RADIUS
