export const getThemeProperties = (theme) => {
    switch (theme) {
        case LIGHT_THEME: {
            return {
                changeThemeTo: getFeatureOption('ProductRegionThemeEnabled', 'Dark', 'region.scaffold'),
                icon: 'icon:region.product/common/darkMode',
                analyticsValue: themeChangeAnalyticsDictionary.DARK_THEME_CLICK,
                title: 'switch.to.dark'
            }
        }
        case SBERBANK1_THEME:
        case PREMIER_THEME:
        case PRIVATE_BANKING_THEME:
        case DARK_THEME: {
            return {
                changeThemeTo: getFeatureOption('ProductRegionThemeEnabled', 'Light', 'region.scaffold'),
                icon: 'icon:region.product/common/lightMode',
                analyticsValue: themeChangeAnalyticsDictionary.LIGHT_THEME_CLICK,
                title: 'switch.to.light'
            }
        }
        default: return void 0
    }
}

export const isDarkTheme = (theme) => [DARK_THEME, SBERBANK1_THEME, PREMIER_THEME, PRIVATE_BANKING_THEME].includes(theme)
